// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./TrustScore.sol";
import "./BetFactory.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Bet
 * @dev Individual prediction market contract using internal wallet system.
 */
contract Bet is ReentrancyGuard {
    enum Status {
        OPEN_FOR_BETS,
        AWAITING_PROOF,
        VOTING,
        COMPLETED,
        CANCELLED
    }
    enum Side {
        NONE,
        YES,
        NO
    }

    struct BetDetails {
        string title;
        string description;
        uint256 bettingDeadline;
        uint256 proofDeadline;
        uint256 votingDeadline;
        uint256 minimumBetAmount;
        uint256 minimumSideStake;
        uint8 minimumTrustScore;
        uint256 minimumVotes;
    }

    struct Participant {
        uint256 yesStake;
        uint256 noStake;
        bool hasWithdrawn;
    }

    BetDetails public details;
    address public creator;
    BetFactory public immutable betFactory;
    TrustScore public immutable trustScoreContract;
    IERC20 public immutable usdcToken;
    IERC20 public immutable proofToken;
    address public immutable feeCollector;

    uint256 public totalYesStake;
    uint256 public totalNoStake;
    uint256 public totalVoteStakeProof;
    string public proofUrl;
    Side public winningSide;
    bool public fundsDistributed;

    uint256 public yesVotes;
    uint256 public noVotes;
    uint256 public participantCount;
    uint256 public totalVotes;

    mapping(address => bool) public hasParticipated;
    mapping(address => Participant) public participants;
    mapping(address => uint256) public voterStakesProof;
    mapping(address => Side) public votes;
    mapping(address => bool) public voted;

    Status private _currentStatus;

    // ======== EVENTS ========

    event BetPlaced(address indexed user, Side position, uint256 amountUsdc);
    event ProofSubmitted(address indexed creator, string proofUrl);
    event VoteCast(address indexed voter, Side vote, uint256 amountProof);
    event BetResolved(Side winningSide);
    event BetCancelled(string reason);
    event FundsWithdrawn(address indexed user, uint256 amountUsdc, uint256 amountProof);

    event BetResolvedSnapshot(
        Side winningSide,
        uint256 totalWinningStake,
        uint256 totalLosingStake,
        uint256 platformFeeAmount,
        uint256 voterRewardPool,
        uint256 winnersPool,
        uint256 winningVoterCount,
        uint256 rewardPerWinningVoter
    );

    // ======== MODIFIERS ========

    modifier onlyCreator() {
        require(msg.sender == creator, "Not creator");
        _;
    }

    modifier atStatus(Status _status) {
        require(_currentStatus == _status, "Invalid status");
        _;
    }

    // ======== CONSTRUCTOR ========

    constructor(
        BetDetails memory _details,
        address _creator,
        address _betFactory,
        address _trustScore,
        address _usdcToken,
        address _proofToken,
        address _feeCollector
    ) {
        require(
            _creator != address(0) &&
                _betFactory != address(0) &&
                _trustScore != address(0) &&
                _usdcToken != address(0) &&
                _proofToken != address(0) &&
                _feeCollector != address(0),
            "Zero address"
        );

        require(
            _details.bettingDeadline < _details.proofDeadline &&
                _details.proofDeadline < _details.votingDeadline,
            "Bad deadlines"
        );

        details = _details;
        creator = _creator;
        betFactory = BetFactory(_betFactory);
        trustScoreContract = TrustScore(_trustScore);
        usdcToken = IERC20(_usdcToken);
        proofToken = IERC20(_proofToken);
        feeCollector = _feeCollector;
        _currentStatus = Status.OPEN_FOR_BETS;
    }

    // ======== VIEW FUNCTIONS ========

    function currentStatus() public view returns (Status) {
        return _currentStatus;
    }

    function getBetDetails() external view returns (BetDetails memory) {
        return details;
    }

    function getBetInfo()
        external
        view
        returns (
            string memory title,
            string memory description,
            uint256 totalYes,
            uint256 totalNo,
            uint256 bettingDeadline,
            uint256 proofDeadline,
            uint256 votingDeadline,
            string memory proof,
            uint256 participantsCount,
            uint256 voters
        )
    {
        return (
            details.title,
            details.description,
            totalYesStake,
            totalNoStake,
            details.bettingDeadline,
            details.proofDeadline,
            details.votingDeadline,
            proofUrl,
            participantCount,
            totalVotes
        );
    }

    function getVoteStats()
        external
        view
        returns (uint256 totalYesVotes, uint256 totalNoVotes, uint256 totalVoteStake)
    {
        return (yesVotes, noVotes, totalVoteStakeProof);
    }

    // ======== CORE ========

    function placeBet(Side _position, uint256 _amountUsdc)
        external
        nonReentrant
        atStatus(Status.OPEN_FOR_BETS)
    {
        require(block.timestamp < details.bettingDeadline, "Betting closed");
        require(_position == Side.YES || _position == Side.NO, "Invalid side");
        require(_amountUsdc >= details.minimumBetAmount, "Stake too low");

        if (details.minimumTrustScore > 0) {
            require(
                trustScoreContract.getScore(msg.sender) >= details.minimumTrustScore,
                "Trust too low"
            );
        }

        (uint256 userUsdcBalance, ) = betFactory.getInternalBalances(msg.sender);
        require(userUsdcBalance >= _amountUsdc, "Insufficient USDC");

        betFactory.transferInternalUsdc(msg.sender, address(this), _amountUsdc, "Bet placement");

        if (!hasParticipated[msg.sender]) {
            hasParticipated[msg.sender] = true;
            participantCount++;
        }

        Participant storage p = participants[msg.sender];
        if (_position == Side.YES) {
            p.yesStake += _amountUsdc;
            totalYesStake += _amountUsdc;
        } else {
            p.noStake += _amountUsdc;
            totalNoStake += _amountUsdc;
        }

        betFactory.factoryLogBetParticipation(msg.sender);
        emit BetPlaced(msg.sender, _position, _amountUsdc);
    }

    function submitProof(string memory _proofUrl)
        external
        onlyCreator
        atStatus(Status.AWAITING_PROOF)
    {
        require(block.timestamp >= details.bettingDeadline, "Betting still open");
        require(block.timestamp < details.proofDeadline, "Proof deadline passed");
        require(bytes(_proofUrl).length > 0, "Empty proof");
        proofUrl = _proofUrl;
        _currentStatus = Status.VOTING;
        emit ProofSubmitted(creator, _proofUrl);
    }

    function vote(Side _vote) external nonReentrant atStatus(Status.VOTING) {
        require(block.timestamp < details.votingDeadline, "Voting closed");
        require(_vote == Side.YES || _vote == Side.NO, "Invalid vote");
        require(
            participants[msg.sender].yesStake == 0 && participants[msg.sender].noStake == 0,
            "Bettors cannot vote"
        );
        require(!voted[msg.sender], "Already voted");
        require(
            trustScoreContract.getScore(msg.sender) >= details.minimumTrustScore,
            "Trust too low"
        );

        uint256 stakeAmt = betFactory.voteStakeAmountProof();
        (, uint256 userProofBalance) = betFactory.getInternalBalances(msg.sender);
        require(userProofBalance >= stakeAmt, "Insufficient PROOF");

        betFactory.transferInternalProof(msg.sender, address(this), stakeAmt, "Vote stake");

        voted[msg.sender] = true;
        votes[msg.sender] = _vote;
        voterStakesProof[msg.sender] += stakeAmt;
        totalVoteStakeProof += stakeAmt;
        totalVotes++;

        if (_vote == Side.YES) yesVotes++;
        else noVotes++;

        betFactory.factoryLogVote(msg.sender);
        emit VoteCast(msg.sender, _vote, stakeAmt);
    }

    // ======== KEEPERS ========

    function checkAndCloseBetting() external {
        if (_currentStatus == Status.OPEN_FOR_BETS && block.timestamp >= details.bettingDeadline) {
            if (totalYesStake >= details.minimumSideStake && totalNoStake >= details.minimumSideStake)
            {
                _currentStatus = Status.AWAITING_PROOF;
            } else {
                _currentStatus = Status.CANCELLED;
                betFactory.factoryLogBetCompletion(creator);
                emit BetCancelled("Minimum side stake not met");
            }
        }
    }

    function checkAndCancelForProof() external {
        if (_currentStatus == Status.AWAITING_PROOF && block.timestamp >= details.proofDeadline) {
            _currentStatus = Status.CANCELLED;
            betFactory.factoryLogBetCompletion(creator);
            emit BetCancelled("Proof deadline missed");
        }
    }

    function checkAndResolve() external nonReentrant {
        if (_currentStatus == Status.VOTING && block.timestamp >= details.votingDeadline) {
            if (totalVotes < details.minimumVotes) {
                _currentStatus = Status.CANCELLED;
                emit BetCancelled("Insufficient public votes");
            } else {
                if (yesVotes > noVotes) {
                    winningSide = Side.YES;
                    _currentStatus = Status.COMPLETED;
                    emit BetResolved(winningSide);
                } else if (noVotes > yesVotes) {
                    winningSide = Side.NO;
                    _currentStatus = Status.COMPLETED;
                    emit BetResolved(winningSide);
                } else {
                    _currentStatus = Status.CANCELLED;
                    emit BetCancelled("Vote tie");
                }
            }
            betFactory.factoryLogBetCompletion(creator);
            withdrawFunds();

            if (_currentStatus == Status.COMPLETED) {
                (
                    ,
                    ,
                    uint256 totalWinningStake,
                    uint256 totalLosingStake,
                    ,
                    ,
                    uint256 platformFeeAmount,
                    uint256 voterRewardPool,
                    uint256 winnersPool,
                    uint256 winningVoterCount,
                    uint256 rewardPerWinningVoter
                ) = this.getResolutionInfo();

                emit BetResolvedSnapshot(
                    winningSide,
                    totalWinningStake,
                    totalLosingStake,
                    platformFeeAmount,
                    voterRewardPool,
                    winnersPool,
                    winningVoterCount,
                    rewardPerWinningVoter
                );
            }
        }
    }

    // ======== INTERNAL DISTRIBUTION ========

    function withdrawFunds() internal {
        require(
            _currentStatus == Status.COMPLETED || _currentStatus == Status.CANCELLED,
            "Bet not finished"
        );
        require(!fundsDistributed, "Already distributed");

        if (_currentStatus == Status.COMPLETED) distributeWinnings();
        else refundAll();
    }

    function distributeWinnings() internal {
        fundsDistributed = true;

        uint256 totalWinning = (winningSide == Side.YES) ? totalYesStake : totalNoStake;
        uint256 totalLosing = (winningSide == Side.YES) ? totalNoStake : totalYesStake;
        require(totalWinning > 0, "No stakes to distribute");

        uint256 platformFeeAmount =
            (totalLosing * betFactory.defaultPlatformFeePercentage()) / 100;
        if (platformFeeAmount > 0) {
            betFactory.transferInternalUsdc(address(this), feeCollector, platformFeeAmount, "Platform fee");
        }

        uint256 losingVoterCount = (winningSide == Side.YES) ? noVotes : yesVotes;
        if (losingVoterCount > 0) {
            uint256 losingProof = losingVoterCount * betFactory.voteStakeAmountProof();
            if (losingProof > 0) {
                betFactory.transferInternalProof(address(this), address(betFactory), losingProof, "Voter losing stake");
            }
        }
    }

    function refundAll() internal {
        fundsDistributed = true;
    }

    // ======== CLAIM FUNCTIONS ========

    function claimWinnings() external nonReentrant {
        require(_currentStatus == Status.COMPLETED, "Not completed");
        require(fundsDistributed, "Not distributed");

        Participant storage p = participants[msg.sender];
        require(p.yesStake > 0 || p.noStake > 0, "No participation");
        require(!p.hasWithdrawn, "Already withdrawn");

        uint256 participantWinningStake =
            (winningSide == Side.YES) ? p.yesStake : p.noStake;
        require(participantWinningStake > 0, "Not winner");

        uint256 totalWinningStake =
            (winningSide == Side.YES) ? totalYesStake : totalNoStake;
        uint256 totalLosingStake =
            (winningSide == Side.YES) ? totalNoStake : totalYesStake;

        uint256 voterRewardAmount =
            (totalLosingStake * betFactory.defaultVoterRewardPercentage()) / 100;
        uint256 platformFeeAmount =
            (totalLosingStake * betFactory.defaultPlatformFeePercentage()) / 100;
        uint256 netPool = totalLosingStake - voterRewardAmount - platformFeeAmount;

        uint256 payout =
            participantWinningStake +
            (netPool * participantWinningStake) / totalWinningStake;

        p.hasWithdrawn = true;
        if (payout > 0) {
            betFactory.transferInternalUsdc(address(this), msg.sender, payout, "Bet winnings");
        }
        emit FundsWithdrawn(msg.sender, payout, 0);
    }

    function claimVoterRewards() external nonReentrant {
        require(
            _currentStatus == Status.COMPLETED || _currentStatus == Status.CANCELLED,
            "Not finished"
        );
        require(voted[msg.sender], "Not voted");
        require(voterStakesProof[msg.sender] > 0, "No stake");

        uint256 proofToReturn;
        uint256 usdcReward;
        uint256 originalStake = voterStakesProof[msg.sender];
        voterStakesProof[msg.sender] = 0;

        if (_currentStatus == Status.COMPLETED) {
            bool votedCorrect = (votes[msg.sender] == winningSide);
            if (votedCorrect) {
                proofToReturn = originalStake;
                uint256 totalLosingStake =
                    (winningSide == Side.YES) ? totalNoStake : totalYesStake;
                uint256 rewardPool =
                    (totalLosingStake * betFactory.defaultVoterRewardPercentage()) / 100;
                uint256 winners =
                    (winningSide == Side.YES) ? yesVotes : noVotes;
                if (rewardPool > 0 && winners > 0) {
                    usdcReward = rewardPool / winners;
                }
            }
        } else {
            proofToReturn = originalStake;
        }

        if (proofToReturn > 0) {
            betFactory.transferInternalProof(address(this), msg.sender, proofToReturn, "Vote stake return");
        }
        if (usdcReward > 0) {
            betFactory.transferInternalUsdc(address(this), msg.sender, usdcReward, "Voter reward");
        }

        emit FundsWithdrawn(msg.sender, usdcReward, proofToReturn);
    }

    function claimRefund() external nonReentrant {
        require(_currentStatus == Status.CANCELLED, "Not cancelled");
        Participant storage p = participants[msg.sender];
        require(!p.hasWithdrawn, "Already withdrawn");

        uint256 refund = p.yesStake + p.noStake;
        require(refund > 0, "No refund");

        p.hasWithdrawn = true;
        betFactory.transferInternalUsdc(address(this), msg.sender, refund, "Bet refund");
        emit FundsWithdrawn(msg.sender, refund, 0);
    }

    // ======== ANALYTICS ========

    function getResolutionInfo()
        external
        view
        returns (
            Status status,
            Side winningSide_,
            uint256 totalWinningStake,
            uint256 totalLosingStake,
            uint256 platformFeePct,
            uint256 voterRewardPct,
            uint256 platformFeeAmount,
            uint256 voterRewardPool,
            uint256 winnersPool,
            uint256 winningVoterCount,
            uint256 rewardPerWinningVoter
        )
    {
        status = _currentStatus;
        winningSide_ = winningSide;

        if (_currentStatus != Status.COMPLETED) {
            return (
                status,
                winningSide_,
                0,
                0,
                betFactory.defaultPlatformFeePercentage(),
                betFactory.defaultVoterRewardPercentage(),
                0,
                0,
                0,
                0,
                0
            );
        }

        totalWinningStake = (winningSide == Side.YES) ? totalYesStake : totalNoStake;
        totalLosingStake = (winningSide == Side.YES) ? totalNoStake : totalYesStake;

        platformFeePct = betFactory.defaultPlatformFeePercentage();
        voterRewardPct = betFactory.defaultVoterRewardPercentage();

        platformFeeAmount = (totalLosingStake * platformFeePct) / 100;
        voterRewardPool = (totalLosingStake * voterRewardPct) / 100;
        winnersPool = totalLosingStake - platformFeeAmount - voterRewardPool;
        winningVoterCount = (winningSide == Side.YES) ? yesVotes : noVotes;
        rewardPerWinningVoter =
            (winningVoterCount > 0) ? voterRewardPool / winningVoterCount : 0;
    }

    function calculateParticipantPayout(address _participant)
        public
        view
        returns (uint256 payout, bool isWinner)
    {
        if (_currentStatus != Status.COMPLETED) return (0, false);
        Participant memory p = participants[_participant];
        if (p.hasWithdrawn || (p.yesStake == 0 && p.noStake == 0)) return (0, false);

        if (winningSide == Side.YES && p.yesStake > 0) isWinner = true;
        else if (winningSide == Side.NO && p.noStake > 0) isWinner = true;
        else return (0, false);

        uint256 stake = (winningSide == Side.YES) ? p.yesStake : p.noStake;
        uint256 totalWinning = (winningSide == Side.YES) ? totalYesStake : totalNoStake;
        uint256 totalLosing = (winningSide == Side.YES) ? totalNoStake : totalYesStake;
        uint256 voterRewardPct = betFactory.defaultVoterRewardPercentage();
        uint256 platformFeePct = betFactory.defaultPlatformFeePercentage();
        uint256 voterRewardAmt = (totalLosing * voterRewardPct) / 100;
        uint256 platformFeeAmt = (totalLosing * platformFeePct) / 100;
        uint256 netPool = totalLosing - voterRewardAmt - platformFeeAmt;

        payout = stake + (netPool * stake) / totalWinning;
    }

    function calculateVoterReward(address _voter)
        public
        view
        returns (uint256 usdcReward, uint256 proofRefund)
    {
        if (!voted[_voter] || voterStakesProof[_voter] == 0) {
            return (0, 0);
        }

        uint256 originalProofStake = voterStakesProof[_voter];
        proofRefund = originalProofStake; // default refund for cancel or win

        if (_currentStatus == Status.CANCELLED) {
            return (0, proofRefund);
        }

        if (_currentStatus != Status.COMPLETED) {
            return (0, 0);
        }

        bool votedCorrectly = (votes[_voter] == winningSide);
        if (!votedCorrectly) {
            proofRefund = 0; // losing voters forfeit proof
            return (0, 0);
        }

        uint256 totalLosingStake =
            (winningSide == Side.YES) ? totalNoStake : totalYesStake;
        uint256 totalVoterRewardPool =
            (totalLosingStake * betFactory.defaultVoterRewardPercentage()) / 100;
        uint256 winningVoterCount =
            (winningSide == Side.YES) ? yesVotes : noVotes;

        if (winningVoterCount > 0 && totalVoterRewardPool > 0) {
            usdcReward = totalVoterRewardPool / winningVoterCount;
        }
    }
}

project/contracts/BetFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./Bet.sol";
import "./TrustScore.sol";
import "./ProofToken.sol"; // Import the concrete contract
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BetFactory
 * @dev Factory for creating and managing Bet contracts.
 * Implements an internal wallet system for USDC and PROOF tokens
 * to optimize user experience by reducing gas and approval steps.
 *
 * IMPORTANT: This factory holds the actual token balances that back the internal
 * ledger (internalUsdcBalance/internalProofBalance). Burns and transfers operate
 * against the factory's real token balance.
 */
contract BetFactory is Ownable, ReentrancyGuard {
    TrustScore public trustScoreContract;
    IERC20 public usdcToken;
    ProofToken public proofToken; // concrete ProofToken
    address public feeCollector;

    address[] public allBets;
    mapping(address => bool) public isBetFromFactory; // Verify Bet contracts created by this factory

    // Internal wallet balances (backed 1:1 by tokens held in this factory)
    mapping(address => uint256) public internalUsdcBalance;
    mapping(address => uint256) public internalProofBalance;

    // Configuration parameters
    uint256 public creationFeeProof;
    uint256 public voteStakeAmountProof;
    uint8 public defaultVoterRewardPercentage;
    uint8 public defaultPlatformFeePercentage;
    uint256 public maxActiveBetsPerUser;                 // Maximum number of active bets a user can create
    mapping(address => uint256) public activeBetsCount;  // Tracks active bets per user

    // Events
    event BetCreated(address indexed betAddress, address indexed creator, string title);
    event FeeProcessed(address indexed payer, uint256 totalFee, uint256 burnAmount, uint256 keepAmount);
    event VoteStakeAmountChanged(uint256 oldAmount, uint256 newAmount);
    event DefaultVoterRewardChanged(uint8 oldPercentage, uint8 newPercentage);
    event DefaultPlatformFeeChanged(uint8 oldPercentage, uint8 newPercentage);
    event MaxActiveBetsChanged(uint256 oldLimit, uint256 newLimit);
    event UsdcDeposited(address indexed user, uint256 amount);
    event ProofDeposited(address indexed user, uint256 amount);
    event UsdcWithdrawn(address indexed user, uint256 amount);
    event ProofWithdrawn(address indexed user, uint256 amount);
    event InternalTransfer(address indexed from, address indexed to, uint256 usdcAmount, uint256 proofAmount, string reason);

    constructor(
        address _trustScore,
        address _usdcToken,
        address _proofToken, // Deployed ProofToken
        address _feeCollector,
        uint256 _creationFeeProof,
        uint256 _initialVoteStakeAmountProof,
        uint256 _maxActiveBets
    )
        Ownable(msg.sender)
    {
        require(_trustScore != address(0), "Zero trustScore");
        require(_usdcToken != address(0), "Zero USDC");
        require(_proofToken != address(0), "Zero PROOF");
        require(_feeCollector != address(0), "Zero feeCollector");
        require(_initialVoteStakeAmountProof > 0, "Vote stake must be > 0");

        trustScoreContract = TrustScore(_trustScore);
        usdcToken = IERC20(_usdcToken);
        proofToken = ProofToken(_proofToken);
        feeCollector = _feeCollector;
        creationFeeProof = _creationFeeProof;
        voteStakeAmountProof = _initialVoteStakeAmountProof;
        maxActiveBetsPerUser = _maxActiveBets;

        // Sensible defaults (changeable by owner)
        defaultVoterRewardPercentage = 5;  // 5%
        defaultPlatformFeePercentage  = 3;  // 3%
    }

    // ========= Internal wallet (deposits/withdrawals) =========

    function depositUsdc(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be > 0");
        require(usdcToken.transferFrom(msg.sender, address(this), _amount), "USDC transfer failed");

        internalUsdcBalance[msg.sender] += _amount;
        emit UsdcDeposited(msg.sender, _amount);
    }

    function depositProof(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be > 0");
        require(proofToken.transferFrom(msg.sender, address(this), _amount), "PROOF transfer failed");

        internalProofBalance[msg.sender] += _amount;
        emit ProofDeposited(msg.sender, _amount);
    }

    function withdrawUsdc(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be > 0");
        require(internalUsdcBalance[msg.sender] >= _amount, "Insufficient internal USDC");

        unchecked { internalUsdcBalance[msg.sender] -= _amount; } // safe: checked above
        require(usdcToken.transfer(msg.sender, _amount), "USDC transfer failed");

        emit UsdcWithdrawn(msg.sender, _amount);
    }

    function withdrawProof(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be > 0");
        require(internalProofBalance[msg.sender] >= _amount, "Insufficient internal PROOF");

        unchecked { internalProofBalance[msg.sender] -= _amount; } // safe: checked above
        require(proofToken.transfer(msg.sender, _amount), "PROOF transfer failed");

        emit ProofWithdrawn(msg.sender, _amount);
    }

    // ========= Internal transfers (called by Bet contracts only) =========

    function transferInternalUsdc(
        address _from,
        address _to,
        uint256 _amount,
        string calldata _reason
    )
        external
        nonReentrant
    {
        require(isBetFromFactory[msg.sender], "Only factory bets");
        require(internalUsdcBalance[_from] >= _amount, "Insufficient internal USDC");

        unchecked {
            internalUsdcBalance[_from] -= _amount;
            internalUsdcBalance[_to]   += _amount;
        }

        emit InternalTransfer(_from, _to, _amount, 0, _reason);
    }

    function transferInternalProof(
        address _from,
        address _to,
        uint256 _amount,
        string calldata _reason
    )
        external
        nonReentrant
    {
        require(isBetFromFactory[msg.sender], "Only factory bets");
        require(internalProofBalance[_from] >= _amount, "Insufficient internal PROOF");

        unchecked {
            internalProofBalance[_from] -= _amount;
            internalProofBalance[_to]   += _amount;
        }

        emit InternalTransfer(_from, _to, 0, _amount, _reason);
    }

    // Read-only helper for front-end
    function getInternalBalances(address _user)
        external
        view
        returns (uint256 usdcBalance, uint256 proofBalance)
    {
        return (internalUsdcBalance[_user], internalProofBalance[_user]);
    }

    // ========= Bet creation =========

    /**
     * @dev Creates a new bet using internal balances (PROOF creation fee).
     * Assumes the factory holds the actual tokens backing the internal balance.
     */
    function createBet(Bet.BetDetails memory _details)
        external
        nonReentrant
        returns (address)
    {
        require(internalProofBalance[msg.sender] >= creationFeeProof, "Insufficient PROOF");
        require(activeBetsCount[msg.sender] < maxActiveBetsPerUser, "Too many active bets");

        // Deduct creation fee from user's internal PROOF balance
        unchecked { internalProofBalance[msg.sender] -= creationFeeProof; }

        // Process fee: burn a portion and send remainder to feeCollector FROM factory's balance
        uint256 burnAmount = (creationFeeProof * proofToken.feeBurnPercentage()) / 100;
        uint256 keepAmount = creationFeeProof - burnAmount;

        if (burnAmount > 0) {
            // Factory already holds real tokens (from deposits). Burn from factory balance.
            proofToken.burn(burnAmount);
        }
        if (keepAmount > 0) {
            require(proofToken.transfer(feeCollector, keepAmount), "PROOF fee transfer failed");
        }

        emit FeeProcessed(msg.sender, creationFeeProof, burnAmount, keepAmount);

        // Deploy the Bet
        Bet newBet = new Bet(
            _details,
            msg.sender,                 // creator
            address(this),              // factory
            address(trustScoreContract),
            address(usdcToken),
            address(proofToken),
            feeCollector
        );

        address newBetAddress = address(newBet);
        allBets.push(newBetAddress);
        isBetFromFactory[newBetAddress] = true;
        activeBetsCount[msg.sender]++;

        // TrustScore hook
        trustScoreContract.logBetCreation(msg.sender);

        emit BetCreated(newBetAddress, msg.sender, _details.title);
        return newBetAddress;
    }

    // ========= Hooks from Bet =========

    function factoryLogBetParticipation(address participant) external {
        require(isBetFromFactory[msg.sender], "Not a Bet");
        trustScoreContract.logBetParticipation(participant);
    }

    function factoryLogVote(address voter) external {
        require(isBetFromFactory[msg.sender], "Not a Bet");
        trustScoreContract.logVote(voter);
    }

    // Called by Bet contract when it's resolved or cancelled
    function factoryLogBetCompletion(address creator) external {
        require(isBetFromFactory[msg.sender], "Not a Bet");
        if (activeBetsCount[creator] > 0) {
            unchecked { activeBetsCount[creator]--; }
        }
    }

    // ========= Admin =========

    function setCreationFee(uint256 _newFee) external onlyOwner {
        creationFeeProof = _newFee;
    }

    function setFeeCollector(address _newCollector) external onlyOwner {
        require(_newCollector != address(0), "Invalid collector");
        feeCollector = _newCollector;
    }

    function setVoteStakeAmount(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Vote stake must be > 0");
        uint256 oldAmount = voteStakeAmountProof;
        voteStakeAmountProof = _amount;
        emit VoteStakeAmountChanged(oldAmount, _amount);
    }

    function setDefaultVoterRewardPercentage(uint8 _percentage) external onlyOwner {
        require(_percentage <= 50, "Voter reward > 50%");
        uint8 oldPercentage = defaultVoterRewardPercentage;
        defaultVoterRewardPercentage = _percentage;
        emit DefaultVoterRewardChanged(oldPercentage, _percentage);
    }

    function setDefaultPlatformFeePercentage(uint8 _percentage) external onlyOwner {
        require(_percentage <= 50, "Platform fee > 50%");
        uint8 oldPercentage = defaultPlatformFeePercentage;
        defaultPlatformFeePercentage = _percentage;
        emit DefaultPlatformFeeChanged(oldPercentage, _percentage);
    }

    function setMaxActiveBetsPerUser(uint256 _limit) external onlyOwner {
        require(_limit > 0, "Limit must be > 0");
        uint256 oldLimit = maxActiveBetsPerUser;
        maxActiveBetsPerUser = _limit;
        emit MaxActiveBetsChanged(oldLimit, _limit);
    }

    // ========= Read helpers =========

    function getBets() external view returns (address[] memory) {
        return allBets;
    }

    function getActiveBetCount(address user) external view returns (uint256) {
        return activeBetsCount[user];
    }

    function getFactoryConfig()
        external
        view
        returns (
            uint256 creationFee,
            uint256 voteStakeAmount,
            uint8 voterRewardPct,
            uint8 platformFeePct,
            uint256 maxActive
        )
    {
        return (
            creationFeeProof,
            voteStakeAmountProof,
            defaultVoterRewardPercentage,
            defaultPlatformFeePercentage,
            maxActiveBetsPerUser
        );
    }
}

